 import React, { useState, useEffect } from 'react';
import './App.css';

const CommentSection = () => {
  const [comments, setComments] = useState([]);
  const [name, setName] = useState('');
  const [commentText, setCommentText] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editReplyId, setEditReplyId] = useState(null);
  const [replyText, setReplyText] = useState({});
  const [warningMessage, setWarningMessage] = useState('');
  const [deleteConfirmation, setDeleteConfirmation] = useState(null);

  // Load comments from localStorage
  useEffect(() => {
    const savedComments = JSON.parse(localStorage.getItem('comments') || '[]');
    setComments(savedComments);
  }, []);

  // Save comments to localStorage
  useEffect(() => {
    localStorage.setItem('comments', JSON.stringify(comments));
  }, [comments]);

  const handleAddComment = (parentId = null) => {
    if (name.trim() && commentText.trim()) {
      const newComment = {
        id: Date.now(),
        name,
        text: commentText,
        date: new Date().toLocaleString(),
        replies: [],
        parentId,
      };

      if (parentId) {
        setComments(addReply(comments, parentId, newComment));
      } else {
        setComments([...comments, newComment]);
      }

      setName('');
      setCommentText('');
      setWarningMessage('');
    } else {
      setWarningMessage('Both name and comment are required!');
    }
  };

  const addReply = (comments, parentId, newReply) => {
    return comments.map(comment => {
      if (comment.id === parentId) {
        return { ...comment, replies: [...comment.replies, newReply] };
      } else if (comment.replies.length > 0) {
        return { ...comment, replies: addReply(comment.replies, parentId, newReply) };
      }
      return comment;
    });
  };

  const handleEditComment = (id, parentId = null) => {
    if (commentText.trim()) {
      const updateComment = (comments) => {
        return comments.map(comment => {
          if (comment.id === id) {
            return { ...comment, text: commentText };
          } else if (comment.replies.length > 0) {
            return { ...comment, replies: updateComment(comment.replies) };
          }
          return comment;
        });
      };

      setComments(updateComment(comments));
      setEditCommentId(null);
      setEditReplyId(null);
      setCommentText('');
      setWarningMessage('');
    } else {
      setWarningMessage('Comment text cannot be empty!');
    }
  };

  const handleDeleteComment = (commentId, parentId = null) => {
    const deleteComment = (comments) => {
      return comments.filter(comment => {
        if (comment.id === commentId) {
          return false;
        }
        if (comment.replies.length > 0) {
          comment.replies = deleteComment(comment.replies);
        }
        return true;
      });
    };

    setComments(deleteComment(comments));
    setDeleteConfirmation(null);
  };

  const renderComments = (commentsToRender, depth = 0) => {
    return commentsToRender.map((comment) => (
      <li key={comment.id} className={`bg-gray-100 p-4 rounded-lg relative shadow-sm mt-4 ${depth > 0 ? 'ml-6' : ''}`}>
        <p className="font-bold text-gray-800">{comment.name} - <span className="font-normal text-gray-600">{comment.date}</span></p>
        {editCommentId === comment.id ? (
          <textarea
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            className="w-full mt-2 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
          />
        ) : (
          <p className="mt-2 text-gray-700">{comment.text}</p>
        )}
        <div className="mt-4 space-x-2">
          <button 
            onClick={() => {
              setEditCommentId(comment.id);
              setCommentText(comment.text);
            }}
            className="text-blue-600 hover:text-blue-800"
          >
            Edit
          </button>
          <button 
            onClick={() => setDeleteConfirmation(comment.id)}
            className="text-blue-600 hover:text-blue-800"
          >
            Delete
          </button>
          <button 
            onClick={() => setReplyText({ ...replyText, [comment.id]: { name: '', text: '' } })}
            className="text-blue-600 hover:text-blue-800"
          >
            Reply
          </button>
        </div>
        {editCommentId === comment.id && (
          <button
            onClick={() => handleEditComment(comment.id)}
            className="mt-2 bg-blue-600 text-white py-1 px-3 rounded-md hover:bg-blue-700 transition duration-300"
          >
            Update Comment
          </button>
        )}
        {replyText[comment.id] && (
          <div className="mt-4 space-y-2">
            <input
              type="text"
              placeholder="Your Name"
              value={replyText[comment.id].name}
              onChange={(e) =>
                setReplyText({ ...replyText, [comment.id]: { ...replyText[comment.id], name: e.target.value } })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <textarea
              placeholder="Your Reply"
              value={replyText[comment.id].text}
              onChange={(e) =>
                setReplyText({ ...replyText, [comment.id]: { ...replyText[comment.id], text: e.target.value } })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
            />
            <button
              onClick={() => {
                handleAddComment(comment.id);
                setReplyText({ ...replyText, [comment.id]: null });
              }}
              className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
            >
              Submit Reply
            </button>
          </div>
        )}
        {comment.replies.length > 0 && (
          <ul className="mt-4 space-y-4">
            {renderComments(comment.replies, depth + 1)}
          </ul>
        )}
      </li>
    ));
  };

  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Comments Section</h2>
      <div className="mb-6 space-y-4">
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <textarea
          placeholder="Add a comment"
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
        />
        <button 
          onClick={() => handleAddComment()}
          className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
        >
          Add Comment
        </button>
        {warningMessage && <p className="text-red-500 text-sm mt-2">{warningMessage}</p>}
      </div>
      <ul className="space-y-6">
        {renderComments(comments)}
      </ul>
      {deleteConfirmation && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg">
            <p className="mb-4">Are you sure you want to delete this comment?</p>
            <div className="flex justify-end space-x-2">
              <button 
                onClick={() => setDeleteConfirmation(null)}
                className="px-4 py-2 bg-gray-200 rounded-md hover:bg-gray-300 transition duration-300"
              >
                Cancel
              </button>
              <button 
                onClick={() => handleDeleteComment(deleteConfirmation)}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition duration-300"
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CommentSection;
