import React, { useState, useEffect } from 'react';
import './App.css';

const CommentSection = () => {
  const [comments, setComments] = useState([]);
  const [name, setName] = useState('');
  const [commentText, setCommentText] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [replyText, setReplyText] = useState({});
  const [warningMessage, setWarningMessage] = useState(''); // New state for warning message

  // Load comments from localStorage
  useEffect(() => {
    const savedComments = JSON.parse(localStorage.getItem('comments')) || [];
    setComments(savedComments);
  }, []);

  // Save comments to localStorage
  useEffect(() => {
    localStorage.setItem('comments', JSON.stringify(comments));
  }, [comments]);

  const handleAddComment = () => {
    if (name.trim() && commentText.trim()) {
      const newComment = {
        id: Date.now(),
        name,
        text: commentText,
        date: new Date().toLocaleString(),
        replies: [],
      };
      setComments([...comments, newComment]);
      setName('');
      setCommentText('');
      setWarningMessage(''); // Clear warning message
    } else {
      setWarningMessage('Both name and comment are required!'); // Set warning message
    }
  };

  const handleEditComment = (id) => {
    if (commentText.trim()) {
      const updatedComments = comments.map((comment) =>
        comment.id === id ? { ...comment, text: commentText } : comment
      );
      setComments(updatedComments);
      setEditCommentId(null);
      setCommentText('');
      setWarningMessage(''); // Clear warning message
    } else {
      setWarningMessage('Comment text cannot be empty!'); // Set warning message for edit
    }
  };

  const handleReply = (id, replyName, replyText) => {
    if (replyName.trim() && replyText.trim()) {
      const updatedComments = comments.map((comment) =>
        comment.id === id
          ? {
              ...comment,
              replies: [
                ...comment.replies,
                {
                  id: Date.now(),
                  name: replyName,
                  text: replyText,
                  date: new Date().toLocaleString(),
                },
              ],
            }
          : comment
      );
      setComments(updatedComments);
      setReplyText({ ...replyText, [id]: '' });
      setWarningMessage(''); // Clear warning message
    } else {
      setWarningMessage('Both name and reply text are required!'); // Set warning message for reply
    }
  };

  const handleDeleteComment = (commentId, replyId = null) => {
    if (replyId) {
      // Delete a reply
      const updatedComments = comments.map((comment) =>
        comment.id === commentId
          ? {
              ...comment,
              replies: comment.replies.filter((reply) => reply.id !== replyId),
            }
          : comment
      );
      setComments(updatedComments);
    } else {
      // Delete a comment
      const updatedComments = comments.filter((comment) => comment.id !== commentId);
      setComments(updatedComments);
    }
  };

  const handleSortByDate = () => {
    const sortedComments = [...comments].sort((a, b) => new Date(a.date) - new Date(b.date));
    setComments(sortedComments);
  };

  return (
    <div className="comment-section">
      <h2>Comments Section</h2>
      <div className="comment-input">
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <textarea
          placeholder="Add a comment"
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={editCommentId ? () => handleEditComment(editCommentId) : handleAddComment}>
          {editCommentId ? 'Update Comment' : 'Add Comment'}
        </button>
        {warningMessage && <p className="warning-message">{warningMessage}</p>} {/* Warning message */}
      </div>
      <button className="sort-button" onClick={handleSortByDate}>Sort by Date</button>
      <ul className="comment-list">
        {comments.map((comment) => (
          <li key={comment.id} className="comment">
            <p><strong>{comment.name}</strong> - {comment.date}</p>
            <p>{comment.text}</p>
            {editCommentId === comment.id ? (
              <textarea
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
              />
            ) : null}
            <div className="comment-actions">
              <button onClick={() => {
                setEditCommentId(comment.id);
                setCommentText(comment.text);
              }}>Edit</button>
              <button onClick={() => handleDeleteComment(comment.id)}>Delete</button>
            </div>
            <div className="reply-input">
              <input
                type="text"
                placeholder="Reply Name"
                value={replyText[comment.id]?.name || ''}
                onChange={(e) =>
                  setReplyText({ ...replyText, [comment.id]: { ...replyText[comment.id], name: e.target.value } })
                }
              />
              <textarea
                placeholder="Reply Text"
                value={replyText[comment.id]?.text || ''}
                onChange={(e) =>
                  setReplyText({ ...replyText, [comment.id]: { ...replyText[comment.id], text: e.target.value } })
                }
              />
              <button
                onClick={() =>
                  handleReply(comment.id, replyText[comment.id]?.name || '', replyText[comment.id]?.text || '')
                }
              >
                Reply
              </button>
            </div>
            <ul className="reply-list">
              {comment.replies.map((reply) => (
                <li key={reply.id} className="reply">
                  <p><strong>{reply.name}</strong> - {reply.date}</p>
                  <p>{reply.text}</p>
                  <div className="reply-actions">
                    <button onClick={() => handleDeleteComment(comment.id, reply.id)}>Delete Reply</button>
                  </div>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CommentSection;
