import React, { useState, useEffect } from 'react';
import './App.css';

const CommentSection = () => {
  const [comments, setComments] = useState([]);
  const [name, setName] = useState('');
  const [commentText, setCommentText] = useState('');
  const [editCommentId, setEditCommentId] = useState(null);
  const [editReplyId, setEditReplyId] = useState(null);
  const [replyText, setReplyText] = useState({});
  const [warningMessage, setWarningMessage] = useState('');

  // Load comments from localStorage
  useEffect(() => {
    const savedComments = JSON.parse(localStorage.getItem('comments')) || [];
    setComments(savedComments);
  }, []);

  // Save comments to localStorage
  useEffect(() => {
    localStorage.setItem('comments', JSON.stringify(comments));
  }, [comments]);

  const handleAddComment = () => {
    if (name.trim() && commentText.trim()) {
      const newComment = {
        id: Date.now(),
        name,
        text: commentText,
        date: new Date().toLocaleString(),
        replies: [],
      };
      setComments([...comments, newComment]);
      setName('');
      setCommentText('');
      setWarningMessage('');
    } else {
      setWarningMessage('Both name and comment are required!');
    }
  };

  const handleEditComment = (id) => {
    if (commentText.trim()) {
      const updatedComments = comments.map((comment) =>
        comment.id === id ? { ...comment, text: commentText } : comment
      );
      setComments(updatedComments);
      setEditCommentId(null);
      setCommentText('');
      setWarningMessage('');
    } else {
      setWarningMessage('Comment text cannot be empty!');
    }
  };

  const handleReply = (id, replyName, replyText) => {
    if (replyName.trim() && replyText.trim()) {
      const updatedComments = comments.map((comment) =>
        comment.id === id
          ? {
              ...comment,
              replies: [
                ...comment.replies,
                {
                  id: Date.now(),
                  name: replyName,
                  text: replyText,
                  date: new Date().toLocaleString(),
                },
              ],
            }
          : comment
      );
      setComments(updatedComments);
      setReplyText({ ...replyText, [id]: '' });
      setWarningMessage('');
    } else {
      setWarningMessage('Both name and reply text are required!');
    }
  };

  const handleEditReply = (commentId, replyId) => {
    if (replyText[commentId]?.text?.trim()) {
      const updatedComments = comments.map((comment) =>
        comment.id === commentId
          ? {
              ...comment,
              replies: comment.replies.map((reply) =>
                reply.id === replyId
                  ? { ...reply, text: replyText[commentId].text }
                  : reply
              ),
            }
          : comment
      );
      setComments(updatedComments);
      setEditReplyId(null);
      setReplyText({ ...replyText, [commentId]: { ...replyText[commentId], text: '' } });
      setWarningMessage('');
    } else {
      setWarningMessage('Reply text cannot be empty!');
    }
  };

  const handleDeleteComment = (commentId, replyId = null) => {
    if (replyId) {
      const updatedComments = comments.map((comment) =>
        comment.id === commentId
          ? {
              ...comment,
              replies: comment.replies.filter((reply) => reply.id !== replyId),
            }
          : comment
      );
      setComments(updatedComments);
    } else {
      const updatedComments = comments.filter((comment) => comment.id !== commentId);
      setComments(updatedComments);
    }
  };

  const handleSortByDate = () => {
    const sortedComments = [...comments].sort((a, b) => new Date(a.date) - new Date(b.date));
    setComments(sortedComments);
  };

  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Comments Section</h2>
      <div className="mb-6 space-y-4">
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <textarea
          placeholder="Add a comment"
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
        />
        <button 
          onClick={editCommentId ? () => handleEditComment(editCommentId) : handleAddComment}
          className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
        >
          {editCommentId ? 'Update Comment' : 'Add Comment'}
        </button>
        {warningMessage && <p className="text-red-500 text-sm mt-2">{warningMessage}</p>}
      </div>
      <button 
        className="block mx-auto mb-6 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
        onClick={handleSortByDate}
      >
        Sort by Date
      </button>
      <ul className="space-y-6">
        {comments.map((comment) => (
          <li key={comment.id} className="bg-gray-100 p-4 rounded-lg relative shadow-sm">
            <p className="font-bold text-gray-800">{comment.name} - <span className="font-normal text-gray-600">{comment.date}</span></p>
            <p className="mt-2 text-gray-700">{comment.text}</p>
            {editCommentId === comment.id ? (
              <textarea
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
                className="w-full mt-2 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
              />
            ) : null}
            <div className="absolute top-4 right-4 space-x-2">
              <button 
                onClick={() => {
                  setEditCommentId(comment.id);
                  setCommentText(comment.text);
                }}
                className="text-blue-600 hover:text-blue-800"
              >
                Edit
              </button>
              <button 
                onClick={() => handleDeleteComment(comment.id)}
                className="text-blue-600 hover:text-blue-800"
              >
                Delete
              </button>
            </div>
            <div className="mt-4 space-y-2">
              <input
                type="text"
                placeholder="Reply Name"
                value={replyText[comment.id]?.name || ''}
                onChange={(e) =>
                  setReplyText({ ...replyText, [comment.id]: { ...replyText[comment.id], name: e.target.value } })
                }
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <textarea
                placeholder="Reply Text"
                value={replyText[comment.id]?.text || ''}
                onChange={(e) =>
                  setReplyText({ ...replyText, [comment.id]: { ...replyText[comment.id], text: e.target.value } })
                }
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
              />
              <button
                onClick={() =>
                  handleReply(comment.id, replyText[comment.id]?.name || '', replyText[comment.id]?.text || '')
                }
                className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
              >
                Reply
              </button>
            </div>
            <ul className="mt-4 space-y-4 pl-6">
              {comment.replies.map((reply) => (
                <li key={reply.id} className="bg-gray-200 p-3 rounded-md relative">
                  <p className="font-bold text-gray-800">{reply.name} - <span className="font-normal text-gray-600">{reply.date}</span></p>
                  {editReplyId === reply.id ? (
                    <textarea
                      value={replyText[comment.id]?.text || ''}
                      onChange={(e) =>
                        setReplyText({
                          ...replyText,
                          [comment.id]: { ...replyText[comment.id], text: e.target.value }
                        })
                      }
                      className="w-full mt-2 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-y"
                    />
                  ) : (
                    <p className="mt-2 text-gray-700">{reply.text}</p>
                  )}
                  <div className="absolute top-3 right-3 space-x-2">
                    {editReplyId === reply.id ? (
                      <button 
                        onClick={() => handleEditReply(comment.id, reply.id)}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        Update Reply
                      </button>
                    ) : (
                      <button 
                        onClick={() => {
                          setEditReplyId(reply.id);
                          setReplyText({
                            ...replyText,
                            [comment.id]: { ...replyText[comment.id], text: reply.text }
                          });
                        }}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        Edit Reply
                      </button>
                    )}
                    <button 
                      onClick={() => handleDeleteComment(comment.id, reply.id)}
                      className="text-blue-600 hover:text-blue-800"
                    >
                      Delete Reply
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CommentSection;
